#include <stdio.h>
#include <stdlib.h>
#include <unistd.h>
#include <sys/wait.h>

void reader_printer(FILE *f, int n, char *msg) {
  char buf[2048];
  size_t sz = sizeof(buf);
  for (int _ = 0; _ < n; _++) {
    getline(&buf, &sz, f);
    printf("%s%s", msg, buf); // Buf probably includes a newline
    usleep(1000);
  }
}

// This tests the behavior of file streams under forks.  A file is opened, then read by a process.
// The process forks and the fork immediately closes.  The parent process discovers the file cursor
// has been reset.  Oops.
int main() {
  FILE *f = fopen("test.c", "r");
  char buf[1024];

  // Read the first line.
  reader_printer(f, 1, "1: ");

  // Now fork and pause until the fork closes
  pid_t pid1, pid2;
  pid1 = fork();
  if (pid1 == 0) {
    reader_printer(f, 8, "2: ");
    exit(0);
  } else {
    pid2 = fork();
    if (pid2 == 0) {
      reader_printer(f, 8, "3: ");
      exit(0);
    }
  }

  // Sleep for a bit to let the cleanup finalize
  waitpid(pid1, NULL, 0);
  waitpid(pid2, NULL, 0);
  usleep(100000);

  // Now let's read the next line
  reader_printer(f, 1, "1: ");

  return 0;
}
